/*
 * @Date: 2020-03-19 04:54:06
 * @LastEditors: lifangdi
 * @LastEditTime: 2020-04-28 00:09:35
 */
import React, { Component } from 'react';
import { withRouter } from 'react-router-dom'
import { ImagePicker, Tabs, Button } from 'antd-mobile';

@withRouter
class Subscribe extends Component {
  state = {
    companionsData: [
      {
        avatorUrl: '',
        name: 'xiaohong',
        id: 1
      },
      {
        avatorUrl: '',
        name: 'xiaoming',
        id: 2
      }
    ],
    bloggersData: [
      {
        id: 1,
        avatorUrl: '',
        name: '拍下美好',
        intro: '旅行摄影博主',
        isSubcribed: true
      },
      {
        id: 2,
        avatorUrl: '',
        name: '港湾美食探索',
        intro: '美食生活博主',
        isSubcribed: true
      }
    ]
  }
  render() {
    const { companionsData, bloggersData } = this.state
    return (
      <div className="subscribe-container">
        <div className="companions">
          <p>同行人</p>
          <div className="companions-container"></div>
          {
            companionsData.map(item => (
              <div className="companions-item">
                <div className="avator" style={{backgroundImage: `url(${item.avatorUrl})`}}></div>
                <div className="name">{item.name}</div>
              </div>
            ))
          }
          <div className="add-compainon">+</div>
        </div>
        {
          bloggersData.length > 0 &&
          <div className="bloggers">
          {
            bloggersData.map(item => (
              <div className="bloggers-item">
                <div className="avator" style={{backgroundImage: `url(${item.avatorUrl})`}}></div>
                <div className="name">{item.name}</div>
                <Button className="subscribe-btn">{item.isSubcribed ? '已关注' : '关注'}</Button>
              </div>
            ))
          }
          </div>
        }

      </div>
    )
  }
}

export default Subscribe;
