import React, { Component } from 'react'
import { withRouter, Link, HashRouter } from 'react-router-dom'
import { TabBar } from 'antd-mobile';
import { MyIcon } from '../Icons'
import './index.less'

@withRouter
class TabNav extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedTab: 'redTab',
      hidden: false,
      fullScreen: false,
    };
  }

  render() {
    const { history } = this.props
    return (
      <div style={this.state.fullScreen ? { position: 'fixed', height: '100%', width: '100%', top: 0 } : { height: 50 }}>
        <TabBar
          unselectedTintColor="#949494"
          tintColor="#4056A1"
          barTintColor="white"
          hidden={this.state.hidden}
        >
          <TabBar.Item
            title="首页"
            key="home"
            icon={<MyIcon type="iconhome" className="tab-icon" />}
            selectedIcon={<MyIcon type="iconhome" className="tab-icon-selected" />}
            selected={this.state.selectedTab === 'blueTab'}
            onPress={() => {
              this.setState({
                selectedTab: 'blueTab',
              });
              history.push({pathname: '/main/home'})
            }}
            data-seed="logId"
          />
          <TabBar.Item
            icon={<MyIcon type="iconmy" className="tab-icon"/>}
            selectedIcon={<MyIcon type="iconmy" className="tab-icon-selected"/>}
            title="我的"
            key="mine"
            selected={this.state.selectedTab === 'redTab'}
            onPress={() => {
              this.setState({
                selectedTab: 'redTab',
              });
              history.push({pathname: '/main/mine'})
            }}
            data-seed="logId1"
          />
        </TabBar>
      </div>
    );
  }
}

export default TabNav
