/*
 * @Date: 2020-03-19 04:54:06
 * @LastEditors: lifangdi
 * @LastEditTime: 2020-04-04 14:29:33
 */
import React, { Component } from 'react';
import { withRouter } from 'react-router-dom'
import { Popover } from 'antd-mobile';
import {MyIcon} from '../../components/Icons'
import './index.less'

const Item = Popover.Item;

@withRouter
class Home extends Component {
  state = {
    filterVisible: false,
    discoveryData: [
      {
        title: 'IFS',
        subTitle: '和妈妈一起买买买',
        imgUrl: 'https://user-images.githubusercontent.com/38416128/78419061-586c5500-7674-11ea-9a52-3c4bde684304.jpeg',
        spend: '97',
        descInfo: '9:00-22:00 营业',
        likeNum: 233,
        type: '购物'
      },
      {
        title: 'Line Friends',
        subTitle: '大陆第二家LINE咖啡馆',
        imgUrl: 'https://user-images.githubusercontent.com/38416128/78419061-586c5500-7674-11ea-9a52-3c4bde684304.jpeg',
        spend: '',
        descInfo: '9:00-22:00 营业',
        likeNum: 233,
        type: '休闲'
      },
      {
        title: 'IFS',
        subTitle: '和妈妈一起买买买',
        imgUrl: 'https://user-images.githubusercontent.com/38416128/78419061-586c5500-7674-11ea-9a52-3c4bde684304.jpeg',
        spend: '',
        descInfo: '9:00-22:00 营业',
        likeNum: 233,
        type: '话剧'
      }
    ]
  }

  renderDiscoveryItem() {
    const { discoveryData } = this.state
    return discoveryData && discoveryData.map(item => (
      <div className="item">
        <div className="type-tag">{item.type}</div>
        <div className="item-img" style={{backgroundImage: 'url(https://user-images.githubusercontent.com/38416128/78419061-586c5500-7674-11ea-9a52-3c4bde684304.jpeg)'}}></div>
        <div className="item-info">
          <p className="info-title">{item.title}</p>
          <p className="info-subtitle">{item.subTitle}</p>
          { item.spend && <p className="spend"><span>{item.spend}</span>元/人</p>}
          <div className="info-desc">
            <span className="open-time">{item.descInfo}</span>
            <div className="like-icon">
              <MyIcon type="iconLike" />{item.likeNum}
            </div>
          </div>
        </div>
      </div>
    ))
  }

  renderFilterItem() {

  }
  onFilterSelect = (opt) => {
    // console.log(opt.props.value);
    this.setState({
      visible: false,
      selected: opt.props.value,
    });
  };
  handleFilterVisibleChange = (visible) => {
    this.setState({
      visible,
    });
  };
  render() {
    const { clientHeight } = this.props

    return (
      <div style={{marginTop: 50, padding: '10px 20px'}}>
        <div className="discovery-header">
          <span className="header-title">个性化推荐</span>
          <Popover mask
            overlayClassName="fortest"
            overlayStyle={{ color: 'currentColor' }}
            visible={this.state.visible}
            overlay={
              <div>aaa</div>
            }
            align={{
              overflow: { adjustY: 0, adjustX: 0 },
              offset: [-10, 0],
            }}
            onVisibleChange={this.handleFilterVisibleChange}
            onSelect={this.onFilterSelect}
          >
            <div style={{
              height: '100%',
              padding: '0 15px',
              marginRight: '-15px',
              display: 'flex',
              alignItems: 'center',
            }}
            >
              <MyIcon type="iconfilter" className="header-icon"></MyIcon>
            </div>
          </Popover>
        </div>
        <div className="discovery-content">
          {this.renderDiscoveryItem()}
        </div>
      </div>
    )
  }
}

export default Home;
