/*
 * @Date: 2020-03-22 16:19:12
 * @LastEditors: lifangdi
 * @LastEditTime: 2020-04-06 00:28:49
 */
import React, { Component } from 'react';
import { withRouter } from 'react-router-dom'
import { Tabs } from 'antd-mobile';
import { MyIcon } from '../../components/Icons'

@withRouter
class Home extends Component {
  renderContent = tab => {
    console.log(tab)
    return (
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', backgroundColor: '#fff' }}>
        <img src={tab.headImgUrl} alt="" />
        <div className="schedule-content">
          <div className="scedule-item">
          <div className="item-img" style={{backgroundImage: `url(${tab.imgUrl})`}}></div>
          <div className="item-info">
            <p className="info-title">{tab.name}</p>
            <p className="info-subtitle">{tab.subTitle}</p>
            { tab.spend && <p className="spend"><span>{tab.spend}</span>元/人</p>}
            <div className="info-desc">
              <span className="open-time">{tab.descInfo}</span>
              <div className="like-icon">
                <MyIcon type="iconLike" />{tab.likeNum}
              </div>
            </div>
          </div>
          </div>
        </div>
        <div>增加一个行程</div>
      </div>
    );
  }

  render() {
    const tabs = [
      {
        title: 'Day 1',
        headImgUrl: 'https://user-images.githubusercontent.com/38416128/78503395-e3467000-7798-11ea-903b-2573731cf187.png',
        scheduleData: [
          {
            name: '东京芝公园',
            tags: [
              {
                name: '人文景点',
                key: 1
              },
              {
                name: '季节赏花',
                key: 2
              }
            ],
            imgUrl: 'https://user-images.githubusercontent.com/38416128/78503521-b050ac00-7799-11ea-9844-d9b9835dc39e.png',
            subTitle: '日本最古老的公园之一',
            spend: '97',
            likeUserInfo: [
              {
                avator: 'https://user-images.githubusercontent.com/38416128/78503841-c2334e80-779b-11ea-9ef0-64440f43c31f.JPG',
                userId: '123'
              }
            ]
          }
        ]
      },
      {
        title: 'Day 2',
        headImgUrl: 'https://user-images.githubusercontent.com/38416128/78503395-e3467000-7798-11ea-903b-2573731cf187.png',
        scheduleData: [
          {
            name: '东京芝公园',
            tags: [
              {
                name: '人文景点',
                key: 1
              },
              {
                name: '季节赏花',
                key: 2
              }
            ],
            imgUrl: 'https://user-images.githubusercontent.com/38416128/78503521-b050ac00-7799-11ea-9844-d9b9835dc39e.png',
            subTitle: '日本最古老的公园之一',
            spend: '97',
            likeUserInfo: [
              {
                avator: 'https://user-images.githubusercontent.com/38416128/78503841-c2334e80-779b-11ea-9ef0-64440f43c31f.JPG',
                userId: '123'
              }
            ]
          }
        ]
      },
      {
        title: 'Day 3',
        headImgUrl: 'https://user-images.githubusercontent.com/38416128/78503395-e3467000-7798-11ea-903b-2573731cf187.png',
        scheduleData: [
          {
            name: '东京芝公园',
            tags: [
              {
                name: '人文景点',
                key: 1
              }
            ],
            imgUrl: 'https://user-images.githubusercontent.com/38416128/78503521-b050ac00-7799-11ea-9844-d9b9835dc39e.png',
            subTitle: '日本最古老的公园之一',
            spend: '97',
            likeUserInfo: [
              {
                avator: 'https://user-images.githubusercontent.com/38416128/78503841-c2334e80-779b-11ea-9ef0-64440f43c31f.JPG',
                userId: '123'
              }
            ]
          }
        ]
      },
    ];
    return (
      <div style={{marginTop: 50}}>
        <Tabs tabs={tabs} renderTabBar={props => <Tabs.DefaultTabBar {...props} page={3} />}>
          {this.renderContent}
        </Tabs>
      </div>
    )
  }
}

export default Home;
