import React, { Component } from 'react'
import { withRouter, Link, HashRouter } from 'react-router-dom'
import { TabBar } from 'antd-mobile';
import { MyIcon } from '../Icons'
import './index.less'

@withRouter
class TabNav extends Component {
  state = {
    // selectedTab: 'redTab',
    hidden: false,
    fullScreen: false,
    tabItem: [
      {
        title: '首页',
        key: 'home',
        iconName: 'iconhome',
        selected: this.props.history.pathname === '/main/home',
        onPressFn: () => {
          console.log(1)
          this.setState({
            selectedTab: 'blueTab',
          });
          this.props.history.push({pathname: '/main/home'})
        }
      },
      {
        title: '行程',
        key: 'schedule',
        iconName: 'iconsuitcase',
        selected: this.props.history.pathname === '/main/schedule',
        onPressFn: () => {
          console.log(1)
          this.setState({
            selectedTab: 'blueTab',
          });
          this.props.history.push({pathname: '/main/home'})
        }
      },
      {
        title: '发现',
        key: 'discovery',
        iconName: 'icondiscovery',
        selected: this.props.history.pathname === '/main/discovery',
        onPressFn: () => {
          console.log(1)
          this.setState({
            selectedTab: 'blueTab',
          });
          this.props.history.push({pathname: '/main/home'})
        }
      },
      {
        title: '我的',
        key: 'mine',
        iconName: 'iconmy',
        selected: this.props.history.pathname === '/main/mine',
        onPressFn: () => {
          this.setState({
            selectedTab: 'blueTab',
          });
          this.props.history.push({pathname: '/main/mine'})
        }
      }
    ]
  }

  // curSelectedTab = () => {
  //   console.log(this.props);
  //   this.state.tabItem.map(item => {
  //     if(item.key === this.props) {
  //       this.setState({

  //       })
  //     }
  //   })
  // }

  renderTabItem = () => {
    return this.state.tabItem.map(item => (
       <TabBar.Item
        title={item.title}
        key={item.key}
        icon={<MyIcon type={item.iconName} className="tab-icon" />}
        selectedIcon={<MyIcon type={item.iconName} className="tab-icon-selected" />}
        selected={item.selected}
        onPress={item.onPressFn}
    />))
  }


  render() {
  console.log(this.props)

    const { history } = this.props
    return (
      <div style={this.state.fullScreen ? { position: 'fixed', height: '100%', width: '100%', top: 0 } : { height: 50 }}>
        <TabBar
          unselectedTintColor="#949494"
          tintColor="#4056A1"
          barTintColor="white"
          hidden={this.state.hidden}
        >
          {this.renderTabItem()}
        </TabBar>
      </div>
    );
  }
}

export default TabNav
