/*
 * @Date: 2020-05-02 14:53:08
 * @LastEditors: lifangdi
 * @LastEditTime: 2020-05-02 14:55:34
 */
import { observable, action, computed } from 'mobx';
import { message } from 'antd-mobile';
import API from '../services/app';

class Activities {
  @observable activitiesList = []
  @observable loading = false

  @action.bound
   async getActivitiesList() {
    this.loading = true;
    return await API.getActivitiesList().then(data => {
      this.activitiesList = data;
      this.loading = false;
    }).catch(err => {
      this.toolList = [];
      this.loading = false;
      message.error(`获取文章列表错误：${err}`, 3);
    });
  }
}

export default Activities;
